};

export const chatNameValidationRules = {
  title: [
    Validator.CHECKS.REQUIRED,
    Validator.CHECKS.ALPHANUMERIC,
    Validator.CHECKS.LENGTH(0, 25),
  ],
};

export const chatIDValidationRules = {
  chatId: [
    Validator.CHECKS.REQUIRED,
  ],
};

